import { isContentEditable } from "@testing-library/user-event/dist/utils";
import { useState } from "react";

export default function Resultados(props) {

  // async function fetchim(queryparams){
  //   try{
  //     let url = "http://openweathermap.org/img/wn/";
  //     const response = await fetch(`${url}{${queryparams}}.png`);
  //     const data = await response.json();    
  //   }catch(e){
  //     console.log(error)
  //   }
  // }
    return (<div>
      <h3>Timezone:{props.items.timezone}</h3>
      <h4>El tiempo en los próximos días será:</h4>
         <ul id="resultados">
          {props.items.daily.map((item) => (
            <li>
              <p>{new Date(item.dt * 1000).toLocaleDateString()}</p>
              <p><img className="tiempoimg" alt="" src={"http://openweathermap.org/img/wn/"+item.weather.icon+".png"} /></p>
              <p>Temp: {(item.temp.day -273.15).toFixed(2)}ºC </p>
              <p>Humedad: {item.humidity}%</p>
              <p>Viento: {item.wind_speed}m/s</p>
            </li>
          )).slice(0,props.numitems)}
        </ul>
      </div>);
}
